import Layout from '../components/Layout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
// import fetch from 'unfetch';
import useSWR from 'swr';
import { useRouter } from 'next/router';

function fetcher(url) {
  return fetch(url).then((r) => r.json());
}

export default function Index() {
  const { query } = useRouter();
  // когда надо чтобы отрабатывало на беке и на фронте юзается это
  const cityReq = `https://${process.env.BASE_API}/api/v1/city/default`;
  // не попадает в браузер, можно пользоваться
  console.log(cityReq, ' process.env.BASE_API');
  const { data, error } = useSWR(() => cityReq, fetcher);
  console.log(data, ' I AM RES ON FRONT');
  // const { data, error } = useSWR(
  //   () => `/api/randomQuote${query.author ? '?author=' + query.author : ''}`,
  //   fetcher
